styleSheet:
QFrame{
border-top-right-radius: 1px;
border-top-left-radius: 1px;
border-bottom-right-radius: 1px;
border-bottom-left-radius: 1px;
border: solid;
border-width: 1px;
border-color:rgba(93, 173, 226, 200);
background-color: rgba(2, 56, 255, 11);
}
QPushButton{
border-radius:0px;
background-color: qlineargradient(spread:pad, x1:1, y1:1, x2:1, y2:0, stop:0 rgba(1, 111, 255, 255), stop:1 rgba(12, 222, 255, 255));
icon-size: 30px;
}

QPushButton:pressed{
background-color: qlineargradient(spread:pad, x1:1, y1:1, x2:1, y2:0, stop:0 rgba(12, 222, 255, 255), stop:1 rgba(1, 111, 255, 255));
} 
QPushButton::menu-indicator{
image:none
}
QPushButton#Close_btn{
border-top-right-radius: 1px;
border-bottom-right-radius: 1px;
}
QPushButton#Stop_btn{
border-top-left-radius: 1px;
border-bottom-left-radius: 1px;
}
QPushButton#Mic_btn{
border-top-left-radius: 1px;
border-bottom-left-radius: 1px;
}
QPushButton#Lang_Button{
border-radius:1px;
font: 57 14px "DM Mono Medium";
color: black;
border: 1px solid rgb(3, 115, 255);
background-color: qlineargradient(spread:pad, x1:1, y1:1, x2:0.977727, y2:0, stop:0 rgba(160, 207, 255, 255), stop:1 rgba(255, 255, 255, 255));	 
}
QPushButton#Lang_Button:hover{
color: qlineargradient(spread:pad, x1:0, y1:0, x2:1, y2:1, stop:0 rgba(63, 63, 63, 255), stop:1 rgba(33, 33, 33, 255));
background-color: qlineargradient(spread:pad, x1:1, y1:1, x2:1, y2:0, stop:0 rgba(207, 207, 207, 240), stop:1 rgba(245, 245, 245, 240));
}
QPushButton:hover{
border: solid;
border-width: 1px;
border-color: rgba(255, 255, 250, 150);
}
QPushButton#Lang_Button:pressed{
background-color:white;
}
 QLabel {
border-radius:4px;
border:none;
background-color: rgb(255, 255, 255);
font: 57 13px "DM Mono Medium";
}
