styleSheet:
QFrame{
	border: 1px solid rgb(93, 173, 226);
	border-top-left-radius: 4px;
 	border-top-right-radius: 4px;
 	border-bottom-right-radius: 9px;
        	border-bottom-left-radius: 9px;
	background-color: rgba(25, 219, 254, 100);
	background-color: rgba(156, 232, 246, 100);
}

 QLabel {
border-radius:4px;
border:none;
background-color: rgb(255, 255, 255);
font: 57 10px "DM Mono Medium";
}


QPushButton{
background:transparent;
border:none;
border-radius:4px;
}

QPushButton::menu-indicator { image: none; }


QPushButton#Lang_Button{

border-radius:5px;
font: 57 14px "DM Mono Medium";
color: black;
border: 1px solid rgb(93, 173, 226);
background-color: rgb(255, 255, 255);
}

QPushButton#Lang_Button:hover{
color: qlineargradient(spread:pad, x1:0, y1:0, x2:1, y2:1, stop:0 rgba(63, 63, 63, 255), stop:1 rgba(33, 33, 33, 255));
background-color: qlineargradient(spread:pad, x1:0.505636, y1:0.221, x2:0.431818, y2:1, stop:0 rgba(89, 255, 255, 255), stop:1 rgba(60, 138, 255, 255));
background-color: qlineargradient(spread:pad, x1:1, y1:1, x2:0.977727, y2:0, stop:0 rgba(160, 207, 255, 255), stop:1 rgba(255, 255, 255, 255));
}

QPushButton:hover{
background-color: white;

}

QPushButton#Lang_Button:pressed{
background-color: qlineargradient(spread:pad, x1:0.767, y1:1, x2:1, y2:0, stop:0 rgba(0, 244, 255, 250), stop:1 rgba(3, 115, 255, 250));
}

QPushButton:pressed{
background-color: qlineargradient(spread:pad, x1:0.767, y1:1, x2:1, y2:0, stop:0 rgba(0, 244, 255, 250), stop:1 rgba(3, 115, 255, 250));"
}
