styleSheet:
QFrame{
border-radius:0px;
	background-color: rgb(211, 233, 238);
border:3px solid black;
	border-top-color: rgba(255, 255, 255, 0);
	border-left-color: rgba(141, 154, 200, 0);
	border-right-color: rgba(255, 255, 255, 0);
	border-bottom-color: qlineargradient(spread:pad, x1:0, y1:0, x2:0, y2:1, stop:0 rgba(171, 189, 192, 255), stop:1 rgba(255, 255, 255, 0));
border-bottom-color: qlineargradient(spread:pad, x1:0, y1:0, x2:0, y2:1, stop:0 rgba(149, 164, 167, 255), stop:1 rgba(211, 233, 238, 255))
}

 QLabel {
border-radius:4px;
border:none;
background-color: rgb(255, 255, 255);
font: 57 10px "DM Mono Medium";
}


QPushButton{
background:transparent;
border:none;
border-radius:4px;
}

QPushButton::menu-indicator { image: none; }

QPushButton#Lang_Button{
border-radius:0px;
	font: 57 12px "DM Mono Medium";
color: black;
border:2px solid black;
background-color: rgb(255, 255, 255);
	border-bottom-color:rgb(211, 233, 238);
	border-left-color: rgb(211, 233, 238);
	border-right-color: transparent;
	border-top-color: rgb(211, 233, 238);
}

QPushButton#Lang_Button:hover{
background-color: qlineargradient(spread:pad, x1:1, y1:1, x2:0.977727, y2:0, stop:0 rgba(160, 207, 255, 255), stop:1 rgba(255, 255, 255, 255));
}

QPushButton:hover{
background-color: rgb(220, 237, 241);
border:3px solid black;
border-bottom-color: qlineargradient(spread:pad, x1:0, y1:0, x2:0, y2:1, stop:0 rgba(149, 164, 167, 255), stop:1 rgba(211, 233, 238, 255));
	border-left-color: rgb(211, 233, 238);
	border-right-color: qlineargradient(spread:pad, x1:0.182, y1:0, x2:1, y2:0, stop:0 rgba(177, 195, 199, 255), stop:1 rgba(211, 233, 238, 255));
	border-top-color: rgb(211, 233, 238);
}

QPushButton#Lang_Button:pressed{
background-color: qlineargradient(spread:pad, x1:0.767, y1:1, x2:1, y2:0, stop:0 rgba(0, 244, 255, 250), stop:1 rgba(3, 115, 255, 250));
}

QPushButton:pressed{
background-color: qlineargradient(spread:pad, x1:0.767, y1:1, x2:1, y2:0, stop:0 rgba(0, 244, 255, 250), stop:1 rgba(3, 115, 255, 250));"
